import React, { useEffect, useState } from'react';
import axios from 'axios';
import Entry from './Entry';
import { useParams } from 'react-router-dom'

function SearchBox() {
  const [query, setQuery] = useState('');
  const linkTo = `search/${query}`;

  function handleChange(event) {
    setQuery(event.target.value);
  }
  return (
    <form onSubmit={e => {e.preventDefault();}}className="searchContainer" method="post">
      <h1>WAFFLES :D</h1>
      <div className="searchBox">
        <input onChange={handleChange} value={query} type="text"/>
        <Link className="searchGlass" to={linkTo}><FontAwesomeIcon icon={faSearch}/></Link>
      </div>
    </form>
  );
}

function Posts() {
  var { q } = useParams();
  const [posts, setPosts] = useState([]);
  useEffect(() => {
    axios.get(`http://localhost:2000/${q}`)
      .then(res => {
        setPosts(res.data)
      })
      .catch(err => {
        console.log(err)
      })
  }, []);
  return (
    <>
    <div className="Posts">
      {posts.map(post => {
        console.log(post)
        return <Entry post={post}/>
      })}
    </div>
    </>
  );
}

export default Posts;
